def max_utility_optim(mu, sigma, target):

    params = cp.Variable(len(mu))

    obj = cp.Minimize(0.5 * target * cp.quad_form(params, sigma) - params.T @ mu)

    cons = [cp.sum(params) == 1, params &gt;= 0]

    prob = cp.Problem(obj, cons)

