def pnl_stress(shocks, x, y, z, weights, intercept):

    coef = lm_coef(x, y, weights, intercept)

    if (intercept): x = sm.add_constant(x)

    result = np.multiply(coef.T, implied_shocks(shocks, x, z, weights))

    return np.ravel(result)


