Usage

library(rolloptim)


n_vars &lt;- 3
n_obs &lt;- 15
x &lt;- matrix(rnorm(n_obs * n_vars), nrow = n_obs, ncol = n_vars)

mu &lt;- roll::roll_mean(x, 5)
sigma &lt;- roll::roll_cov(x, width = 5)



Minimize variance
\[
\begin{aligned}
\begin{array}{rrcl}
\displaystyle\min&\frac{1}{2}\mathbf{w}^T\Sigma\mathbf{w}\\
\textrm{s.t.}&\mathbf{w}^{T}e&=&1
\end{array}
\end{aligned}
\]
To incorporate these conditions into one equation, introduce new variables \(\lambda_{i}\) that are the Lagrange multipliers and define a new function \(\mathcal{L}\) as follows:
\[
\begin{aligned}
\mathcal{L}(\mathbf{w},\lambda)&=\frac{1}{2}\mathbf{w}^{T}\Sigma\mathbf{w}-\lambda_{1}(\mathbf{w}^{T}e-1)
\end{aligned}
\]
Then, to minimize this function, take derivatives with respect to \(w\) and Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial w}&=\mathbf{w}\Sigma-\lambda_{1}e=0\\
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial \lambda_{1}}&=\mathbf{w}e^T-1=0
\end{aligned}
\]
Simplify the equations above in matrix form and solve for the Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\begin{bmatrix}
\Sigma & e \\
e^{T} & 0
\end{bmatrix}
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
0 \\
1
\end{bmatrix}
\\
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
\Sigma & e \\
e^{T} & 0
\end{bmatrix}^{-1}
\begin{bmatrix}
0 \\
1
\end{bmatrix}
\end{aligned}
\]

roll_min_var(sigma)



Maximize mean
\[
\begin{aligned}
\begin{array}{rrcl}
\displaystyle\min&-\mathbf{w}^{T}\mu\\
\textrm{s.t.}&\mathbf{w}^{T}e&=&1
\end{array}
\end{aligned}
\]
To incorporate these conditions into one equation, introduce new variables \(\lambda_{i}\) that are the Lagrange multipliers and define a new function \(\mathcal{L}\) as follows:
\[
\begin{aligned}
\mathcal{L}(\mathbf{w},\lambda)&=-\mathbf{w}^{T}\mu-\lambda_{1}(\mathbf{w}^{T}e-1)
\end{aligned}
\]
Then, to minimize this function, take derivatives with respect to \(w\) and Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial w}&=-\mu-\lambda_{1}e=0\\
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial \lambda_{1}}&=\mathbf{w}e^T-1=0
\end{aligned}
\]
Simplify the equations above in matrix form and solve for the Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\begin{bmatrix}
-\mu & e \\
e^{T} & 0
\end{bmatrix}
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
0 \\
1
\end{bmatrix}
\\
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
-\mu & e \\
e^{T} & 0
\end{bmatrix}^{-1}
\begin{bmatrix}
0 \\
1
\end{bmatrix}
\end{aligned}
\]

roll_max_mean(mu)



Maximize utility
\[
\begin{aligned}
\begin{array}{rrcl}
\displaystyle\min&\frac{1}{2}\delta(\mathbf{w}^{T}\Sigma\mathbf{w})-\mu^{T}\mathbf{w}\\
\textrm{s.t.}&e^T\mathbf{w}&=&1
\end{array}
\end{aligned}
\]
To incorporate these conditions into one equation, introduce new variables \(\lambda_{i}\) that are the Lagrange multipliers and define a new function \(\mathcal{L}\) as follows:
\[
\begin{aligned}
\mathcal{L}(\mathbf{w},\lambda)&=\frac{1}{2}\mathbf{w}^{T}\Sigma\mathbf{w}-\mu^{T}\mathbf{w}-\lambda_{1}(\mathbf{w}^{T}e-1)
\end{aligned}
\]
Then, to minimize this function, take derivatives with respect to \(w\) and Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial w}&=\mathbf{w}\Sigma-\mu^{T}-\lambda_{1}e=0\\
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial \lambda_{1}}&=\mathbf{w}e^T-1=0
\end{aligned}
\]
Simplify the equations above in matrix form and solve for the Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\begin{bmatrix}
\Sigma & e \\
e^{T} & 0
\end{bmatrix}
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
\mu^{T} \\
1
\end{bmatrix}
\\
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
\Sigma & e \\
e^{T} & 0
\end{bmatrix}^{-1}
\begin{bmatrix}
\mu^{T} \\
1
\end{bmatrix}
\end{aligned}
\]

roll_max_utility(mu, sigma)



Minimize residual sum of squares
\[
\begin{aligned}
\begin{array}{rrcl}
\displaystyle\min&\frac{1}{2}\delta(\mathbf{w}^{T}X^{T}X\mathbf{w})-X^{T}y\mathbf{w}\\
\textrm{s.t.}&e^T\mathbf{w}&=&1
\end{array}
\end{aligned}
\]
To incorporate these conditions into one equation, introduce new variables \(\lambda_{i}\) that are the Lagrange multipliers and define a new function \(\mathcal{L}\) as follows:
\[
\begin{aligned}
\mathcal{L}(\mathbf{w},\lambda)&=\frac{1}{2}\mathbf{w}^{T}X^{T}X\mathbf{w}-X^{T}y\mathbf{w}-\lambda_{1}(\mathbf{w}^{T}e-1)
\end{aligned}
\]
Then, to minimize this function, take derivatives with respect to \(w\) and Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial w}&=\mathbf{w}X^{T}X-X^{T}y-\lambda_{1}e=0\\
\frac{\partial\mathcal{L}(\mathbf{w},\lambda)}{\partial \lambda_{1}}&=\mathbf{w}e^T-1=0
\end{aligned}
\]
Simplify the equations above in matrix form and solve for the Lagrange multipliers \(\lambda_{i}\):
\[
\begin{aligned}
\begin{bmatrix}
X^{T}X & e \\
e^{T} & 0
\end{bmatrix}
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
X^{T}y \\
1
\end{bmatrix}
\\
\begin{bmatrix}
\mathbf{w} \\
-\lambda_{1}
\end{bmatrix}
&=
\begin{bmatrix}
X^{T}X & e \\
e^{T} & 0
\end{bmatrix}^{-1}
\begin{bmatrix}
X^{T}y \\
1
\end{bmatrix}
\end{aligned}
\]

roll_min_rss(xx, xy)



