def max_mean_optim(mu, sigma, target):

    params = cp.Variable(len(mu))

    obj = cp.Maximize(params.T @ mu)

    cons = [cp.sum(params) == 1, params &gt;= 0,
            cp.quad_form(params, sigma) &lt;= target ** 2]

    prob = cp.Problem(obj, cons)

    prob.solve()

    return params.value


