implied_shocks &lt;- function(shocks, x, z, weights) {

    beta &lt;- solve(crossprod(z, diag(weights) %*% z)) %*% crossprod(z, diag(weights) %*% x)

    result &lt;- shocks %*% beta

    return(result)

}


